// src/pages/AddContent.js
import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { ChevronLeft } from 'lucide-react';
import { api } from '../services/api';

export const AddContent = () => {
  const { locationId, type } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    images: []
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formDataToSend = new FormData();
      formDataToSend.append('title', formData.title);
      formDataToSend.append('description', formData.description);
      formDataToSend.append('type', type);

      Array.from(formData.images).forEach(file => {
        formDataToSend.append('images', file);
      });

      await api.createContent(locationId, formDataToSend);
      navigate(-1);
    } catch (error) {
      console.error('Erreur lors de la création:', error);
    }
  };

  return (
    <div>
      <div className="bg-[#4f5b93] text-white p-4 flex items-center">
        <button onClick={() => navigate(-1)} className="mr-4">
          <ChevronLeft size={24} />
        </button>
        <h1 className="text-xl font-semibold">
          Ajouter une fiche {type === 'measure' ? 'de repères & mesures' : 'de classification'}
        </h1>
      </div>

      <div className="p-4">
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block mb-2 text-gray-700">Titre</label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
              className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-[#4f5b93]"
              required
            />
          </div>

          <div>
            <label className="block mb-2 text-gray-700">Description</label>
            <textarea
              value={formData.description}
              onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
              className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-[#4f5b93]"
              rows={4}
            />
          </div>

          <div>
            <label className="block mb-2 text-gray-700">Images</label>
            <input
              type="file"
              multiple
              accept="image/*"
              onChange={(e) => setFormData(prev => ({ ...prev, images: e.target.files }))}
              className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-[#4f5b93]"
            />
          </div>

          <button
            type="submit"
            className="w-full bg-[#4f5b93] text-white p-3 rounded hover:bg-[#3f4973] transition-colors"
          >
            Créer
          </button>
        </form>
      </div>
    </div>
  );
};
