// src/pages/LocationDetail.js
import React, { useState, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Plus, Search, ChevronLeft, Trash2, Edit, ChevronDown, ChevronUp } from 'lucide-react';
import { api } from '../services/api';

export const LocationDetail = () => {
  const { locationId, systemId } = useParams();
  console.log('LocationDetail Params:', { locationId, systemId }); 
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('measure');
  const [content, setContent] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [expandedItems, setExpandedItems] = useState(new Set());

  const loadContent = useCallback(async () => {
    if (!locationId) {
      setError('ID de localisation manquant');
      setLoading(false);
      return;
    }

    try {
      setLoading(true);
      const data = await api.getLocationContent(locationId, activeTab);
      setContent(Array.isArray(data) ? data : []);
      setError(null);
    } catch (error) {
      console.error('Erreur:', error);
      setError('Erreur lors du chargement du contenu');
      setContent([]);
    } finally {
      setLoading(false);
    }
  }, [locationId, activeTab]);

  React.useEffect(() => {
    loadContent();
  }, [loadContent]);

  const handleTabChange = async (tab) => {
    setActiveTab(tab);
  };

  const handleDelete = async (contentId) => {
    if (!contentId) {
      console.error('ID de contenu manquant');
      return;
    }

    if (window.confirm('Voulez-vous vraiment supprimer cette fiche ?')) {
      try {
        await api.deleteContent(contentId);
        await loadContent();
      } catch (error) {
        console.error('Erreur lors de la suppression:', error);
        setError('Erreur lors de la suppression de la fiche');
      }
    }
  };

  const handleEdit = (contentId) => {
    console.log('Navigating to edit with params:', {
      systemId,
      locationId,
      activeTab,
      contentId
    });

    if (!contentId || !locationId || !systemId || !activeTab) {
      console.error('Missing required params:', {
        contentId,
        locationId,
        systemId,
        activeTab
      });
      return;
    }

    const editUrl = `/system/${systemId}/location/${locationId}/content/${activeTab}/edit/${contentId}`;
    console.log('Edit URL:', editUrl);
    navigate(editUrl);
  };

  const handleBack = () => {
    navigate(`/system/${systemId}`);
  };

  const toggleItem = (itemId) => {
    setExpandedItems(prev => {
      const newSet = new Set(prev);
      if (newSet.has(itemId)) {
        newSet.delete(itemId);
      } else {
        newSet.add(itemId);
      }
      return newSet;
    });
  };

  if (!locationId || !systemId) {
    return (
      <div className="p-4 text-red-600">
        Erreur: Paramètres manquants
      </div>
    );
  }

  if (loading) {
    return <div className="p-4">Chargement...</div>;
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="bg-[#4f5b93] text-white p-4 flex items-center">
        <button onClick={handleBack} className="mr-4">
          <ChevronLeft size={24} />
        </button>
        <h1 className="text-xl font-semibold">Détails de la localisation</h1>
      </div>

      <div className="flex border-b">
        <button
          className={`flex-1 py-3 px-4 ${activeTab === 'measure' ? 'border-b-2 border-[#4f5b93] text-[#4f5b93]' : 'text-gray-500'}`}
          onClick={() => handleTabChange('measure')}
        >
          Repères & Mesures
        </button>
        <button
          className={`flex-1 py-3 px-4 ${activeTab === 'classification' ? 'border-b-2 border-[#4f5b93] text-[#4f5b93]' : 'text-gray-500'}`}
          onClick={() => handleTabChange('classification')}
        >
          Classifications
        </button>
      </div>

      <div className="p-4">
        <div className="relative mb-4">
          <Search className="absolute left-3 top-3 text-gray-400" size={20} />
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            placeholder="Rechercher une fiche..."
            className="w-full p-2 pl-10 rounded-lg border border-gray-300"
          />
        </div>

        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
            {error}
          </div>
        )}

        <div className="grid gap-4">
          {content
            .filter(item => item.title.toLowerCase().includes(searchTerm.toLowerCase()))
            .map((item) => (
              <div key={item._id} className="bg-white rounded-lg shadow p-4 relative">
                <div 
                  className="flex justify-between items-center cursor-pointer"
                  onClick={() => toggleItem(item._id)}
                >
                  <h3 className="text-lg font-semibold text-[#4f5b93]">{item.title}</h3>
                  <div className="flex items-center gap-2">
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        handleEdit(item._id);
                      }}
                      className="text-blue-500 hover:text-blue-700"
                    >
                      <Edit size={20} />
                    </button>
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDelete(item._id);
                      }}
                      className="text-red-500 hover:text-red-700"
                    >
                      <Trash2 size={20} />
                    </button>
                    {expandedItems.has(item._id) ? (
                      <ChevronUp className="text-gray-500" size={20} />
                    ) : (
                      <ChevronDown className="text-gray-500" size={20} />
                    )}
                  </div>
                </div>

                {expandedItems.has(item._id) && (
                  <div className="mt-4">
                    {item.description && (
                      <div 
                        className="text-gray-600 mb-2 prose max-w-none"
                        dangerouslySetInnerHTML={{ __html: item.description }}
                      />
                    )}
                    {item.images && item.images.length > 0 && (
                      <div className="grid grid-cols-2 gap-2 mt-2">
                        {item.images.map((image, index) => (
                          <img
                            key={index}
                            src={image.url}
                            alt={image.caption}
                            className="rounded w-full h-40 object-cover"
                          />
                        ))}
                      </div>
                    )}
                  </div>
                )}
              </div>
            ))}
        </div>
      </div>

      <button
        onClick={() => {
          console.log('Navigation params:', { systemId, locationId, activeTab });
          if (!locationId) {
            console.error('locationId is missing');
            return;
          }
          navigate(`/system/${systemId}/location/${locationId}/content/${activeTab}`);
        }}
        className="fixed bottom-6 right-6 bg-[#4f5b93] text-white rounded-full p-4 shadow-lg hover:bg-[#3f4973] transition-colors"
      >
        <Plus size={24} />
      </button>
    </div>
  );
};

export default LocationDetail;
