// src/pages/EditContent.js
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import { ChevronLeft } from 'lucide-react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { api } from '../services/api';

export const EditContent = () => {
    const { systemId, locationId, type, contentId } = useParams();
    const navigate = useNavigate();
    const location = useLocation();
    const quillRef = useRef(null);

    console.log('EditContent mounted with params:', { systemId, locationId, type, contentId });
    console.log('Current location:', location);

    const [formData, setFormData] = useState({
      title: '',
      description: '',
      images: []
    });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [existingImages, setExistingImages] = useState([]);

    // Vérification des paramètres obligatoires
    useEffect(() => {
      if (!contentId || !locationId || !systemId || !type) {
        console.error('Missing required params:', {
          contentId,
          locationId,
          systemId,
          type
        });
        setError('Paramètres manquants');
        setTimeout(() => navigate('/'), 2000);
      }
    }, [contentId, locationId, systemId, type, navigate]);

    // Ajoutez cet effet pour charger les données existantes
    useEffect(() => {
      const loadExistingContent = async () => {
        try {
          console.log('Loading content data with ID:', contentId);
          const data = await api.getContent(contentId);
          console.log('Content data loaded:', data);

          if (data) {
            setFormData({
              title: data.title || '',
              description: data.description || '',
              images: []
            });
            setExistingImages(data.images || []);
            setError(null);
          }
        } catch (err) {
          console.error('Error loading content:', err);
          setError('Erreur lors du chargement du contenu');
        } finally {
          setLoading(false);
        }
      };

      if (contentId) {
        loadExistingContent();
      }
    }, [contentId]);

const handleSubmit = async (e) => {
    e.preventDefault();

    if (!locationId || !contentId) {
      setError('Paramètres manquants');
      return;
    }

    setLoading(true);
    try {
      const formDataToSend = new FormData();
      formDataToSend.append('title', formData.title);
      formDataToSend.append('description', formData.description);
      formDataToSend.append('type', type);
      formDataToSend.append('locationId', locationId);

      // Ajouter les nouvelles images
      if (formData.images && formData.images.length > 0) {
        Array.from(formData.images).forEach(file => {
          formDataToSend.append('images', file);
        });
      }

      // Ajouter les images existantes
      formDataToSend.append('existingImages', JSON.stringify(existingImages));

      console.log('Updating content with:', {
        contentId,
        title: formData.title,
        type,
        locationId
      });

      const result = await api.updateContent(contentId, formDataToSend);
      console.log('Content updated:', result);

      // Redirection après succès
      navigate(`/system/${systemId}/location/${locationId}`);
    } catch (error) {
      console.error('Error updating content:', error);
      setError(error.message || 'Erreur lors de la mise à jour');
    } finally {
      setLoading(false);
    }
  };

  const handleImageChange = (e) => {
    const files = e.target.files;
    if (files && files.length > 0) {
      setFormData(prev => ({
        ...prev,
        images: files
      }));
    }
  };

  const handleRemoveExistingImage = (index) => {
    setExistingImages(prev => prev.filter((_, i) => i !== index));
  };

  // Créer les URLs pour la prévisualisation des nouvelles images
  const previewImages = formData.images && Array.from(formData.images).map(file => URL.createObjectURL(file));

  // Nettoyer les URLs de prévisualisation lors du démontage du composant
  useEffect(() => {
    return () => {
      previewImages?.forEach(url => URL.revokeObjectURL(url));
    };
  }, [previewImages]);

  const handleCancel = () => {
    navigate(`/system/${systemId}/location/${locationId}`);
  };

  if (loading && !error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-xl text-gray-600">Chargement...</div>
      </div>
    );
  }

  if (error && (!systemId || !locationId)) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
          {error}
          <p className="mt-2">Redirection en cours...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="bg-[#4f5b93] text-white p-4 flex items-center sticky top-0 z-50">
        <button onClick={handleCancel} className="mr-4">
          <ChevronLeft size={24} />
        </button>
        <h1 className="text-lg sm:text-xl font-semibold truncate">
          {type === 'measure' ? 'Repères & Mesures' : 'Classification'}
        </h1>
      </div>

      <div className="p-2 sm:p-4">
        <form onSubmit={handleSubmit} className="space-y-4 sm:space-y-6">
          {/* Titre */}
          <div>
            <label className="block text-gray-700 font-medium mb-1 sm:mb-2">Titre</label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
              className="w-full p-2 sm:p-3 border rounded-lg text-base"
              placeholder="Entrez le titre de la fiche"
              required
            />
          </div>

          {/* Éditeur */}
          <div>
            <label className="block text-gray-700 font-medium mb-1 sm:mb-2">Description</label>
            <div className="bg-white rounded-lg border">
              <ReactQuill 
                value={formData.description}
                onChange={(content) => setFormData(prev => ({ ...prev, description: content }))}
                className="h-48 sm:h-64 mb-12"
                modules={{
                  toolbar: [
                    [{ 'header': [1, 2, false] }],
                    ['bold', 'italic'],
                    [{'list': 'ordered'}, {'list': 'bullet'}],
                    ['link'],
                    ['clean']
                  ]
                }}
              />
            </div>
          </div>

          {/* Images */}
          <div>
            <label className="block text-gray-700 font-medium mb-1 sm:mb-2">Images</label>
            <input
              type="file"
              multiple
              accept="image/*"
              onChange={handleImageChange}
              className="w-full p-2 border rounded-lg text-sm sm:text-base"
            />
          </div>

          {/* Boutons */}
          <div className="flex gap-2 sm:gap-4 fixed bottom-0 left-0 right-0 p-2 sm:p-4 bg-white border-t">
            <button
              type="button"
              onClick={handleCancel}
              className="flex-1 bg-gray-200 text-gray-700 p-3 rounded-lg text-sm sm:text-base"
            >
              Annuler
            </button>
            <button
              type="submit"
              disabled={loading}
              className="flex-1 bg-[#4f5b93] text-white p-3 rounded-lg text-sm sm:text-base"
            >
              {loading ? '...' : 'Enregistrer'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditContent;
