// src/pages/EditContent.js
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import { ChevronLeft } from 'lucide-react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { api } from '../services/api';

export const EditContent = () => {
  const { systemId, locationId, type, contentId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const quillRef = useRef(null);

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    images: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [existingImages, setExistingImages] = useState([]);

  // Charger les données existantes
  useEffect(() => {
    console.log('Paramètres EditContent:', { contentId, locationId, systemId, type });

    const loadContent = async () => {
      try {
        setLoading(true);
        console.log('Loading content, params:', { contentId, locationId, systemId, type });

        const data = await api.getContent(contentId);
        console.log('Content loaded successfully:', data);

        if (data) {
          setFormData({
            title: data.title || '',
            description: data.description || '',
            images: []
          });
          setExistingImages(data.images || []);
          setError(null); // Clear any previous errors
        }
      } catch (error) {
        console.error('Error loading content details:', error);
        setError('Erreur lors du chargement du contenu');
      } finally {
        setLoading(false);
      }
    };

    if (contentId) {
      loadContent();
    } else {
      console.error('No contentId provided');
      setError('ID de contenu manquant');
    }
  }, [contentId, locationId, systemId, type]);

const handleSubmit = async (e) => {
    e.preventDefault();

    if (!locationId || !contentId) {
      setError('Paramètres manquants');
      return;
    }

    setLoading(true);
    try {
      const formDataToSend = new FormData();
      formDataToSend.append('title', formData.title);
      formDataToSend.append('description', formData.description);
      formDataToSend.append('type', type);
      formDataToSend.append('locationId', locationId);

      // Ajouter les nouvelles images
      if (formData.images && formData.images.length > 0) {
        Array.from(formData.images).forEach(file => {
          formDataToSend.append('images', file);
        });
      }

      // Ajouter les images existantes
      formDataToSend.append('existingImages', JSON.stringify(existingImages));

      console.log('Updating content with:', {
        contentId,
        title: formData.title,
        type,
        locationId
      });

      const result = await api.updateContent(contentId, formDataToSend);
      console.log('Content updated:', result);

      // Redirection après succès
      navigate(`/system/${systemId}/location/${locationId}`);
    } catch (error) {
      console.error('Error updating content:', error);
      setError(error.message || 'Erreur lors de la mise à jour');
    } finally {
      setLoading(false);
    }
  };

  const handleImageChange = (e) => {
    const files = e.target.files;
    if (files && files.length > 0) {
      setFormData(prev => ({
        ...prev,
        images: files
      }));
    }
  };

  const handleRemoveExistingImage = (index) => {
    setExistingImages(prev => prev.filter((_, i) => i !== index));
  };

  // Créer les URLs pour la prévisualisation des nouvelles images
  const previewImages = formData.images && Array.from(formData.images).map(file => URL.createObjectURL(file));

  // Nettoyer les URLs de prévisualisation lors du démontage du composant
  useEffect(() => {
    return () => {
      previewImages?.forEach(url => URL.revokeObjectURL(url));
    };
  }, [previewImages]);

  const handleCancel = () => {
    navigate(`/system/${systemId}/location/${locationId}`);
  };

  if (loading && !error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-xl text-gray-600">Chargement...</div>
      </div>
    );
  }

  if (error && (!systemId || !locationId)) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
          {error}
          <p className="mt-2">Redirection en cours...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="bg-[#4f5b93] text-white p-4 flex items-center">
        <button onClick={handleCancel} className="mr-4">
          <ChevronLeft size={24} />
        </button>
        <h1 className="text-xl font-semibold">
          Modifier la fiche {type === 'measure' ? 'de repères & mesures' : 'de classification'}
        </h1>
      </div>

      <div className="p-4 max-w-4xl mx-auto">
        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-gray-700 font-medium mb-2">Titre</label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
              className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-[#4f5b93] focus:border-transparent"
              placeholder="Entrez le titre de la fiche"
              required
            />
          </div>

          <div>
            <label className="block text-gray-700 font-medium mb-2">Description</label>
            <div className="bg-white">
              <ReactQuill 
                ref={quillRef}
                value={formData.description}
                onChange={(content) => setFormData(prev => ({ ...prev, description: content }))}
                modules={{
                  toolbar: {
                    container: [
                      [{ 'header': [1, 2, false] }],
                      ['bold', 'italic', 'underline', 'strike', 'blockquote'],
                      [{'list': 'ordered'}, {'list': 'bullet'}, {'indent': '-1'}, {'indent': '+1'}],
                      ['link', 'image'],
                      ['clean']
                    ],
                  },
                  clipboard: {
                    matchVisual: false,
                  },
                }}
                formats={[
                  'header',
                  'bold', 'italic', 'underline', 'strike', 'blockquote',
                  'list', 'bullet', 'indent',
                  'link', 'image'
                ]}
                theme="snow"
                className="h-64 mb-12"
                placeholder="Entrez la description détaillée..."
              />
            </div>
          </div>

          <div>
            <label className="block text-gray-700 font-medium mb-2">Images existantes</label>
            {existingImages.length > 0 && (
              <div className="grid grid-cols-2 gap-4 mt-4">
                {existingImages.map((image, index) => (
                  <div key={index} className="relative">
                    <img
                      src={image.url}
                      alt={image.caption || `Image ${index + 1}`}
                      className="w-full h-40 object-cover rounded-lg"
                    />
                    <button
                      type="button"
                      onClick={() => handleRemoveExistingImage(index)}
                      className="absolute top-2 right-2 bg-red-500 text-white rounded-full p-1 hover:bg-red-600"
                    >
                      ×
                    </button>
                  </div>
                ))}
              </div>
            )}

            <label className="block text-gray-700 font-medium mt-4 mb-2">Ajouter de nouvelles images</label>
            <input
              type="file"
              multiple
              accept="image/*"
              onChange={handleImageChange}
              className="w-full p-2 border rounded-lg"
            />
            {previewImages && previewImages.length > 0 && (
              <div className="grid grid-cols-2 gap-4 mt-4">
                {previewImages.map((url, index) => (
                  <img
                    key={index}
                    src={url}
                    alt={`Nouvelle image ${index + 1}`}
                    className="w-full h-40 object-cover rounded-lg"
                  />
                ))}
              </div>
            )}
          </div>

          <div className="flex gap-4">
            <button
              type="button"
              onClick={handleCancel}
              className="flex-1 bg-gray-200 text-gray-700 p-3 rounded-lg hover:bg-gray-300 transition-colors"
            >
              Annuler
            </button>
            <button
              type="submit"
              disabled={loading}
              className={`flex-1 bg-[#4f5b93] text-white p-3 rounded-lg hover:bg-[#3f4973] transition-colors
                ${loading ? 'opacity-50 cursor-not-allowed' : ''}`}
            >
              {loading ? 'Mise à jour en cours...' : 'Mettre à jour'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditContent;
