// src/pages/AddContent.js
import React, { useState } from 'react';
import { Header } from '../components/Header';

export const AddContent = () => {
  const [formData, setFormData] = useState({
    type: '',
    title: '',
    description: '',
    images: []
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Données du formulaire:', formData);
    // Ici vous pourrez ajouter la logique pour sauvegarder les données
  };

  return (
    <div>
      <Header title="Ajouter du contenu" />
      <div className="p-4">
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block mb-2 text-gray-700">Type de contenu</label>
            <select
              name="type"
              value={formData.type}
              onChange={handleInputChange}
              className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
            >
              <option value="">Sélectionnez un type</option>
              <option value="measure">Repère/Mesure</option>
              <option value="classification">Classification</option>
            </select>
          </div>

          <div>
            <label className="block mb-2 text-gray-700">Titre</label>
            <input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleInputChange}
              className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
              placeholder="Entrez un titre"
            />
          </div>

          <div>
            <label className="block mb-2 text-gray-700">Description</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleInputChange}
              className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
              rows={4}
              placeholder="Entrez une description"
            />
          </div>

          <div>
            <label className="block mb-2 text-gray-700">Images</label>
            <input
              type="file"
              accept="image/*"
              multiple
              onChange={(e) => {
                setFormData(prev => ({
                  ...prev,
                  images: Array.from(e.target.files)
                }));
              }}
              className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
            />
          </div>

          <button
            type="submit"
            className="w-full bg-primary text-white p-3 rounded hover:bg-primary/90 transition-colors"
          >
            Enregistrer
          </button>
        </form>
      </div>
    </div>
  );
};
