// src/pages/AddContent.js
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import { ChevronLeft } from 'lucide-react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { api } from '../services/api';

export const AddContent = () => {
  const { systemId, locationId, type } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const quillRef = useRef(null);

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    images: []
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Logs complets pour le débogage
  useEffect(() => {
    console.log('Current URL:', window.location.href);
    console.log('Location state:', location);
    console.log('URL Params:', { systemId, locationId, type });

    // Vérification stricte des paramètres
    if (!systemId || !locationId || locationId === 'undefined' || !type) {
      console.error('Paramètres invalides:', { systemId, locationId, type });
      setError('Paramètres invalides');
      setTimeout(() => {
        navigate('/');
      }, 2000);
      return;
    }
  }, [systemId, locationId, type, navigate, location]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Vérification supplémentaire des paramètres
    if (!locationId || locationId === 'undefined') {
      console.error('ID de localisation invalide:', locationId);
      setError('ID de localisation invalide');
      return;
    }

    if (!systemId) {
      console.error('ID du système invalide:', systemId);
      setError('ID du système invalide');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('title', formData.title);
      formDataToSend.append('description', formData.description);
      formDataToSend.append('type', type);

      // Assurez-vous que locationId est une chaîne valide
      if (typeof locationId === 'string' && locationId.length > 0) {
        formDataToSend.append('locationId', locationId);
      } else {
        throw new Error('ID de localisation invalide');
      }

      console.log('Sending FormData with:', {
        locationId,
        type,
        title: formData.title
      });

      if (formData.images && formData.images.length > 0) {
        Array.from(formData.images).forEach(file => {
          formDataToSend.append('images', file);
        });
      }

      const result = await api.createContent(locationId, formDataToSend);
      console.log('Content created:', result);

      navigate(`/system/${systemId}/location/${locationId}`);
    } catch (error) {
      console.error('Erreur détaillée:', error);
      setError(error.message || 'Erreur lors de la création de la fiche');
    } finally {
      setLoading(false);
    }
  };

  const handleImageChange = (e) => {
    const files = e.target.files;
    if (files && files.length > 0) {
      setFormData(prev => ({
        ...prev,
        images: files
      }));
    }
  };

  // Créer les URLs pour la prévisualisation des images
  const previewImages = formData.images && Array.from(formData.images).map(file => URL.createObjectURL(file));

  // Nettoyer les URLs de prévisualisation lors du démontage du composant
  useEffect(() => {
    return () => {
      previewImages?.forEach(url => URL.revokeObjectURL(url));
    };
  }, [previewImages]);

  const handleCancel = () => {
    navigate(`/system/${systemId}/location/${locationId}`);
  };

  if (error && (!systemId || !locationId)) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
          {error}
          <p className="mt-2">Redirection en cours...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="bg-[#4f5b93] text-white p-4 flex items-center">
        <button onClick={handleCancel} className="mr-4">
          <ChevronLeft size={24} />
        </button>
        <h1 className="text-xl font-semibold">
          Ajouter une fiche {type === 'measure' ? 'de repères & mesures' : 'de classification'}
        </h1>
      </div>

      <div className="p-4 max-w-4xl mx-auto">
        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-gray-700 font-medium mb-2">Titre</label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
              className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-[#4f5b93] focus:border-transparent"
              placeholder="Entrez le titre de la fiche"
              required
            />
          </div>

          <div>
            <label className="block text-gray-700 font-medium mb-2">Description</label>
            <div className="bg-white">
              <ReactQuill 
                ref={quillRef}
                value={formData.description}
                onChange={(content) => setFormData(prev => ({ ...prev, description: content }))}
                modules={{
                  toolbar: {
                    container: [
                      [{ 'header': [1, 2, false] }],
                      ['bold', 'italic', 'underline', 'strike', 'blockquote'],
                      [{'list': 'ordered'}, {'list': 'bullet'}, {'indent': '-1'}, {'indent': '+1'}],
                      ['link', 'image'],
                      ['clean']
                    ],
                  },
                  clipboard: {
                    matchVisual: false,
                  },
                }}
                formats={[
                  'header',
                  'bold', 'italic', 'underline', 'strike', 'blockquote',
                  'list', 'bullet', 'indent',
                  'link', 'image'
                ]}
                theme="snow"
                className="h-64 mb-12"
                placeholder="Entrez la description détaillée..."
              />
            </div>
          </div>

          <div>
            <label className="block text-gray-700 font-medium mb-2">Images</label>
            <input
              type="file"
              multiple
              accept="image/*"
              onChange={handleImageChange}
              className="w-full p-2 border rounded-lg"
            />
            {previewImages && previewImages.length > 0 && (
              <div className="grid grid-cols-2 gap-4 mt-4">
                {previewImages.map((url, index) => (
                  <img
                    key={index}
                    src={url}
                    alt={`Aperçu ${index + 1}`}
                    className="w-full h-40 object-cover rounded-lg"
                  />
                ))}
              </div>
            )}
          </div>

          <div className="flex gap-4">
            <button
              type="button"
              onClick={handleCancel}
              className="flex-1 bg-gray-200 text-gray-700 p-3 rounded-lg hover:bg-gray-300 transition-colors"
            >
              Annuler
            </button>
            <button
              type="submit"
              disabled={loading}
              className={`flex-1 bg-[#4f5b93] text-white p-3 rounded-lg hover:bg-[#3f4973] transition-colors
                ${loading ? 'opacity-50 cursor-not-allowed' : ''}`}
            >
              {loading ? 'Création en cours...' : 'Créer'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddContent;
