import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ChevronLeft } from 'lucide-react';
import { api } from '../services/api';

export const ViewContent = () => {
  const { systemId, locationId, contentId } = useParams();
  const navigate = useNavigate();
  const [content, setContent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadContent = async () => {
      try {
        const data = await api.getContent(contentId);
        setContent(data);
      } catch (error) {
        console.error('Error loading content:', error);
        setError('Erreur lors du chargement de la fiche');
      } finally {
        setLoading(false);
      }
    };

    loadContent();
  }, [contentId]);

  const handleBack = () => {
    navigate(`/system/${systemId}/location/${locationId}`);
  };

  if (loading) {
    return <div className="p-4">Chargement...</div>;
  }

  if (error || !content) {
    return (
      <div className="p-4 text-red-800">
        {error || 'Fiche non trouvée'}
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="bg-[#4f5b93] text-white p-4 flex items-center">
        <button onClick={handleBack} className="mr-4">
          <ChevronLeft size={24} />
        </button>
        <h1 className="text-xl font-semibold">{content.title}</h1>
      </div>

      <div className="p-4 max-w-4xl mx-auto">
        <div className="bg-white rounded-lg shadow p-6">
          {content.description && (
            <div 
              className="text-gray-600 mb-6 prose max-w-none"
              dangerouslySetInnerHTML={{ __html: content.description }}
            />
          )}

          {content.images && content.images.length > 0 && (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {content.images.map((image, index) => (
                <div key={index} className="relative">
                  <img
                    src={image.url}
                    alt={image.caption || `Image ${index + 1}`}
                    className="w-full h-auto rounded-lg shadow"
                  />
                  {image.caption && (
                    <p className="mt-2 text-sm text-gray-500 text-center">
                      {image.caption}
                    </p>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ViewContent;
