import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ChevronLeft } from 'lucide-react';
import { api } from '../services/api';
import 'react-quill/dist/quill.snow.css'; // Import des styles Quill

export const ViewContent = () => {
  const { systemId, locationId, contentId } = useParams();
  const navigate = useNavigate();
  const [content, setContent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadContent = async () => {
      try {
        const data = await api.getContent(contentId);
        setContent(data);
      } catch (error) {
        console.error('Error loading content:', error);
        setError('Erreur lors du chargement de la fiche');
      } finally {
        setLoading(false);
      }
    };

    loadContent();
  }, [contentId]);

  const handleBack = () => {
    navigate(`/system/${systemId}/location/${locationId}`);
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-xl text-gray-600">Chargement...</div>
      </div>
    );
  }

  if (error || !content) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
          {error || 'Fiche non trouvée'}
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="bg-[#4f5b93] text-white p-4 flex items-center">
        <button onClick={handleBack} className="mr-4">
          <ChevronLeft size={24} />
        </button>
        <h1 className="text-xl font-semibold">{content.title}</h1>
      </div>

      <div className="p-4 max-w-4xl mx-auto">
        <div className="bg-white rounded-lg shadow p-6">
          {content.description && (
            <div 
              className="text-gray-700 prose prose-lg max-w-none ql-editor"
              dangerouslySetInnerHTML={{ __html: content.description }}
            />
          )}

          {content.images && content.images.length > 0 && (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-6">
              {content.images.map((image, index) => (
                <div key={index} className="relative">
                  <img
                    src={image.url}
                    alt={image.caption || `Image ${index + 1}`}
                    className="w-full h-auto rounded-lg shadow-lg"
                  />
                  {image.caption && (
                    <p className="mt-2 text-sm text-gray-500 text-center">
                      {image.caption}
                    </p>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      <style jsx>{`
        .ql-editor {
          padding: 0;
        }
        .ql-editor h1 {
          font-size: 2em;
          font-weight: bold;
          margin-bottom: 0.5em;
        }
        .ql-editor h2 {
          font-size: 1.5em;
          font-weight: bold;
          margin-bottom: 0.5em;
        }
        .ql-editor p {
          margin-bottom: 1em;
        }
        .ql-editor ul, .ql-editor ol {
          margin-bottom: 1em;
          padding-left: 2em;
        }
        .ql-editor ul li {
          list-style-type: disc;
        }
        .ql-editor ol li {
          list-style-type: decimal;
        }
        .ql-editor strong {
          font-weight: bold;
        }
        .ql-editor em {
          font-style: italic;
        }
        .ql-editor blockquote {
          border-left: 4px solid #ccc;
          padding-left: 1em;
          margin-bottom: 1em;
        }
      `}</style>
    </div>
  );
};

export default ViewContent;
