import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { ChevronLeft } from 'lucide-react';

const Header = ({ title, showBack = true }) => {
  const navigate = useNavigate();
  const location = useLocation();

  const handleBack = () => {
    // Logique pour la navigation hiérarchique
    const path = location.pathname;

    if (path.includes('/content/')) {
      // Si on est dans une fiche, retourner à la liste des fiches
      const baseUrl = path.split('/content/')[0];
      navigate(baseUrl);
    } else if (path.includes('/location/')) {
      // Si on est dans une localisation, retourner au système
      const systemId = path.split('/system/')[1]?.split('/')[0];
      if (systemId) navigate(`/system/${systemId}`);
    } else if (path.includes('/system/')) {
      // Si on est dans un système, retourner à la liste des systèmes
      navigate('/');
    } else {
      // Fallback au comportement par défaut
      navigate(-1);
    }
  };

  return (
    <div className="fixed top-0 left-0 right-0 z-50 bg-[#4f5b93]">
      {/* En-tête avec hauteur fixe incluant la safe area */}
      <div 
        className="flex items-center h-[calc(env(safe-area-inset-top)+4rem)] pt-[env(safe-area-inset-top)]"
      >
        <div className="flex items-center px-4 w-full h-16">
          {showBack && (
            <button 
              onClick={handleBack}
              className="mr-3"
            >
              <ChevronLeft size={24} className="text-white" />
            </button>
          )}
          <h1 className="text-xl font-semibold text-white flex-1 truncate">
            {title}
          </h1>
        </div>
      </div>
    </div>
  );
};

export default Header;
